import React from 'react';

function Features() {
  return (
    <div style={styles.page}>
      <h2>Our Features</h2>
      <p>Explore the amazing features of our app.</p>
    </div>
  );
}

const styles = {
  page: {
    textAlign: 'center',
    marginTop: '20px',
    fontFamily: 'Arial, sans-serif',
  },
};

export default Features;
