import React from 'react';

function Services() {
  return (
    <div style={styles.page}>
      <h2>Our Services</h2>
      <p>Check out the services we provide.</p>
    </div>
  );
}

const styles = {
  page: {
    textAlign: 'center',
    marginTop: '20px',
    fontFamily: 'Arial, sans-serif',
  },
};

export default Services;
